MultiLanguageSpellData['fr'][86470] = {name = "Maîtrise", additional_info = "[q]Vous permet de bénéficier du score de maîtrise.\n\nEruption stellaire\nEclipse totale\n Augmente le bonus aux dégâts d'Eclipse de 16%. Chaque point de Maîtrise augmente le bonus de 2.0% supplémentaires\n\n\n\nMutilation\nDéfenseur sauvage\n Augmente les dégâts absorbés par votre technique Défense sauvage de 32%. Chaque point de Maîtrise augmente le montant de dégâts absorbé de 4% supplémentaires.\n\nGriffes rasoir\n Augmente les dégâts infligés par vos techniques de félin qui font saigner de 25%. Chaque point de Maîtrise augmente les dégâts de saignement de 3.1% supplémentaires\n\n\n\nPrompte guérison\nHarmonie\n Vos sorts de soins directs sont augmentés de 10% et l'incantation de vos sorts de soins directs vous confère un bonus supplémentaire de 10% aux soins périodiques pendant 10 sec. Chaque point de Maîtrise augmente chaque bonus de 1.25% supplémentaires\n\n Sélectionnez une spécialisation de talents afin d'activer le bonus du score de maîtrise. Consultez le panneau de vue d'ensemble des talents pour plus d'informations."}
MultiLanguageSpellData['fr'][86471] = {name = "Maîtrise", additional_info = "[q]Vous permet de bénéficier du score de maîtrise.\n\nFrappe au cœur\nBouclier de sang\n Chaque fois que vous vous soignez avec Frappe de mort alors que vous êtes en Présence de sang, vous bénéficiez d'un bouclier qui absorbe un montant de dégâts égal à 50% du montant de points de vie rendu. Chaque point de Maîtrise améliore l'absorption de 6.25% supplémentaires\n\n\n\nFrappe de givre\nCoeur gelé\n Augmente tous les dégâts de Givre infligés de 16%. Chaque point de Maîtrise augmente les dégâts de Givre de 2.0% supplémentaires\n\n\n\nFrappe du Fléau\nLame-d'effroi\n Augmente tous les dégâts d'Ombre infligés de 20%. Chaque point de Maîtrise augmente les dégâts d'Ombre de 2.5% supplémentaires\n\n Sélectionnez une spécialisation de talents afin d'activer le bonus du score de maîtrise. Consultez le panneau de vue d'ensemble des talents pour plus d'informations."}
MultiLanguageSpellData['fr'][86472] = {name = "Maîtrise", additional_info = "[q]Vous permet de bénéficier du score de maîtrise.\n\nIntimidation\nMaître des bêtes\n Augmente les dégâts infligés par vos familiers de 13.4%. Chaque point de Maîtrise augmente les dégâts de 1.67% supplémentaires\n\n\n\nVisée\nCarquois sauvage\n Confère 16.8% de chances à vos attaques à distance d'également lancer instantanément un tir à distance supplémentaire. Chaque point de Maîtrise augmente ces chances de 2.1% supplémentaires\n\n\n\nTir explosif\nEssence de la vipère\n Augmente tous les dégâts magiques que vous infligez de 8%. Chaque point de Maîtrise augmente les dégâts magiques de 1.0% supplémentaires\n\n Sélectionnez une spécialisation de talents afin d'activer le bonus du score de maîtrise. Consultez le panneau de vue d'ensemble des talents pour plus d'informations."}
MultiLanguageSpellData['fr'][86473] = {name = "Maîtrise", additional_info = "[q]Vous permet de bénéficier du score de maîtrise.\n\nBarrage des Arcanes\nAdepte du mana\n Augmente tous les dégâts des sorts infligés d'un maximum de 12%, en fonction du mana qui reste au mage. Chaque point de Maîtrise augmente les dégâts infligés de 1.5% supplémentaires au maximum\n\n\n\nExplosion pyrotechnique\nBrûlure éclair\n Augmente les dégâts infligés par tous vos effets de feu périodiques de 22.4%. Chaque point de Maîtrise augmente les dégâts périodiques infligés de 2.8% supplémentaires\n\n\n\nInvocation d'un élémentaire d'eau\nBrûlure du givre\n Tous vos sorts infligent des dégâts augmentés de 20% aux cibles gelées. Chaque point de Maîtrise augmente les dégâts de 2.5% supplémentaires\n\n Sélectionnez une spécialisation de talents afin d'activer le bonus du score de maîtrise. Consultez le panneau de vue d'ensemble des talents pour plus d'informations."}
MultiLanguageSpellData['fr'][86474] = {name = "Maîtrise", additional_info = "[q]Vous permet de bénéficier du score de maîtrise.\n\nHorion sacré\nSoins illuminés\n Vos sorts de soins directs placent également sur votre cible un bouclier d'absorption d'une valeur de 12% du montant soigné qui dure 15 sec. Chaque point de Maîtrise augmente le montant absorbé de 1.50% supplémentaires\n\n\n\nBouclier du vengeur\nRempart divin\n Augmente vos chances de bloquer les attaques de mêlée de 18%. Chaque point de Maîtrise augmente les chances de bloquer de 2.25% supplémentaires\n\n\n\nVerdict du templier\nMain de lumière\n Verdict du templier, Frappe du croisé et Tempête divine infligent 16.8% de dégâts supplémentaires sous forme de dégâts du Sacré. Chaque point de Maîtrise augmente les dégâts de 2.1% supplémentaires\n\n Sélectionnez une spécialisation de talents afin d'activer le bonus du score de maîtrise. Consultez le panneau de vue d'ensemble des talents pour plus d'informations."}
MultiLanguageSpellData['fr'][86475] = {name = "Maîtrise", additional_info = "[q]Vous permet de bénéficier du score de maîtrise.\n\nPénitence\nDiscipline du bouclier\n Augmente l'efficacité de tous vos sorts d'absorption des dégâts de 20%. Chaque point de Maîtrise augmente l’efficacité des absorptions de 2.5% supplémentaires\n\n\n\nMot sacré : Châtier\nEcho de la Lumière\n Vos sorts de soins directs rendent 10% de points de vie supplémentaires en 6 sec. Chaque point de Maîtrise confère 1.25% de soins supplémentaires en 6 sec\n\n\n\nFouet mental\nPuissance des orbes d’ombre\n Augmente les dégâts infligés par vos Orbes d'ombre de 11.6% supplémentaires. Chaque point de Maîtrise augmente les dégâts de 1.45% supplémentaires\n\n Sélectionnez une spécialisation de talents afin d'activer le bonus du score de maîtrise. Consultez le panneau de vue d'ensemble des talents pour plus d'informations."}
MultiLanguageSpellData['fr'][86476] = {name = "Maîtrise", additional_info = "[q]Vous permet de bénéficier du score de maîtrise.\n\nEstropier\nPoisons violents\n Augmente les dégâts infligés par vos poisons de 28%. Chaque point de Maîtrise augmente les dégâts de 3.5% supplémentaires\n\n\n\nDéluge de lames\nMain gauche\n Vos attaques de la main droite ont 16% de chances de vous octroyer une attaque qui inflige des dégâts d'un montant égal à 100% de ceux d'une attaque de la main droite. Chaque point de Maîtrise augmente ces chances de 2.00% supplémentaires\n\n\n\nPas de l'ombre\nBourreau\n Augmente les dégâts infligés par tous vos coups de grâce ainsi que l'efficacité de votre technique Débiter de 20%. Chaque point de Maîtrise augmente les dégâts de 0.1% supplémentaires\n\n Sélectionnez une spécialisation de talents afin d'activer le bonus du score de maîtrise. Consultez le panneau de vue d'ensemble des talents pour plus d'informations."}
MultiLanguageSpellData['fr'][86477] = {name = "Maîtrise", additional_info = "[q]Vous permet de bénéficier du score de maîtrise.\n\nOrage\nSurcharge élémentaire\n Confère 16% de chances qu’une Surcharge élémentaire se produise, ce qui permet à un de vos sorts Eclair, Chaîne d’éclairs ou Explosion de lave de déclencher une copie de ce sort sur la même cible qui ne coûte pas de mana, inflige 75% des dégâts normaux et ne génère pas de menace. Chaque point de Maîtrise augmente les chances de Surcharge élémentaire de 2% supplémentaires\n\n\n\nFouet de lave\nEléments amplifiés\n Augmente les dégâts de Feu, de Givre et de Nature infligés de 20%. Chaque point de Maîtrise augmente les dégâts de 2.5% supplémentaires\n\n\n\nBouclier de terre\nSoins profonds\n Augmente la puissance de vos sorts de soins directs d’un maximum de 24%, en fonction des points de vie actuels de votre cible (les cibles aux points de vie plus bas sont davantage soignées). Chaque point de Maîtrise augmente les soins directs d’un maximum de 3.0% supplémentaires\n\n Sélectionnez une spécialisation de talents afin d'activer le bonus du score de maîtrise. Consultez le panneau de vue d'ensemble des talents pour plus d'informations."}
MultiLanguageSpellData['fr'][86478] = {name = "Maîtrise", additional_info = "[q]Vous permet de bénéficier du score de maîtrise.\n\nAffliction instable\nAfflictions puissantes\n Augmente tous les dégâts d'Ombre périodiques que vous infligez de 13%. Chaque point de Maîtrise augmente les dégâts d’Ombre périodiques de 1.63% supplémentaires\n\n\n\nInvocation : gangregarde\nMaître démonologue\n Augmente les dégâts infligés par vos serviteurs démons et les dégâts que vous infligez lorsque vous êtes transformé en démon de 18.4%. Chaque point de Maîtrise augmente les dégâts de 2.3% supplémentaires\n\n\n\nConflagration\nApocalypse ardente\n Augmente tous les dégâts de Feu que vous infligez de 10.8%. Chaque point de Maîtrise augmente les dégâts de Feu de 1.35% supplémentaires\n\n Sélectionnez une spécialisation de talents afin d'activer le bonus du score de maîtrise. Consultez le panneau de vue d'ensemble des talents pour plus d'informations."}
MultiLanguageSpellData['fr'][86479] = {name = "Maîtrise", additional_info = "[q]Vous permet de bénéficier du score de maîtrise.\n\nFrappe mortelle\nFrappes opportunistes\n Votre Maîtrise confère 17.6% de chances à vos attaques de mêlée de déclencher instantanément une attaque de mêlée supplémentaire infligeant 100% des dégâts normaux. Chaque point de Maîtrise augmente ces chances de 2.2% supplémentaires\n\n\n\nSanguinaire\nFureur déchaînée\n Votre Maîtrise augmente les bénéfices apportés par les techniques qui vont font Enrager ou nécessitent d’être Enragé de 44.8%. Chaque point de Maîtrise augmente les effets d’Enrager de 5.60% supplémentaires\n\n\n\nHeurt de bouclier\nBlocage critique\n Votre Maîtrise augmente vos chances de bloquer de 12% et vos chances de réussir un blocage critique de 12%. Chaque point de Maîtrise augmente vos chances de blocage de 1.5% et de blocage critique de 1.5%\n\n Sélectionnez une spécialisation de talents afin d'activer la Maîtrise. Consultez le panneau de vue d'ensemble des talents pour plus d'informations."}
