MultiLanguageSpellData['fr'][90980] = {name = "Enchaînement", additional_info = "Allonge\nProchaine attaque en mêlée\n[q]Inflige 150% des dégâts de mêlée normaux à un ennemi et à ses alliés les plus proches, affectant jusqu'à 3 cibles."}
MultiLanguageSpellData['fr'][90983] = {name = "[q3]Arrivée des Naaru", additional_info = "3 s d'incantation 15\n[q]Ce cristal semble être un appareil d'enregistrement quelconque. Il diffuse un message dans une langue que vous ne parvenez pas à déchiffrer complètement, mais vous comprenez qu'il s'agit d'un enregistrement ou d'une reconstitution de la première rencontre entre les érédars et les naaru.\n[q3]Arrivée des Naaru\nLié quand ramassé\nJouet\n[q2]Utilise: Assister à un enregistrement de l'arrivée des naaru. (3 Min Recharge)"}
MultiLanguageSpellData['fr'][90984] = {name = "[q3]La dernière relique d'Argus", additional_info = "3 s d'incantation 15\n[q]Avant qu'ils ne viennent vivre en Azeroth ou en Draenor, les draeneï, qui s'appellaient encore érédar à cette époque, vivaient sur Argus. Lorsque Sargeras découvrit Argus, le prophète Velen fuit avec une partie de son peuple, qui devint alors les draeneï. Les autres érédars rejoignirent la Légion ardente. Ce simple cristal provient d'Argus, il fut emporté lorsque Velen fuit la planète. Il y a une inscription au-dessous, sur laquelle on peut lire : « Mac'Aree me manque. »\n[q3]La dernière relique d'Argus\nLié quand ramassé\n[q2]Utilise: Vous téléporte exactement où vous voulez aller… si vous n'êtes pas trop regardant. (12 Hrs Recharge)\n[q]\"Une des rares choses que les draeneï ont pu emporter lorsqu'ils ont fui Argus.\""}
