MultiLanguageSpellData['fr'][91209] = {name = "[q0]Aiguière de sang de jormungar", additional_info = "3 s d'incantation 5\n[q]Ce flacon en verre contient le sang glacé et acide de l'un des grands vers jormungars du Norfendre. Selon la légende, les nérubiens utilisèrent les jormungars pour creuser les nombreux passages souterrains et les grottes des grandes cités d'Azjol-Nérub.\n[q0]Aiguière de sang de jormungar\nLié quand ramassé"}
MultiLanguageSpellData['fr'][91211] = {name = "[q0]Bande griffonnée du vizir", additional_info = "3 s d'incantation 5\n[q]Cet étrange objet semble être un morceau de parchemin aux proportions gigantesques dont le matériau ressemble à un croisement entre le papyrus et le cuir. Il est recouvert de diagrammes et d'inscriptions nérubiennes rouges et cramoisis. « Unhhh-tikk-a-shisss. Kka shir. Na-l bkah a-shisss. Kreesh kik ik ta. Sohk-a tik hiss a-k-k-k. »\n[q0]Bande griffonnée du vizir\nLié quand ramassé"}
