MultiLanguageSpellData['fr'][91214] = {name = "[q4]Bénédiction du Dieu très ancien", additional_info = "3 s d'incantation 15\n[q]Pwhn'guul i ghawl'fwata ryiu wgah uul'gwan h'iwn guu'lal. Pwhn'guul i ghawl'fwata ryiu wgah uul'gwan h'iwn guu'lal. Pwhn'guul i ghawl'fwata ryiu wgah uul'gwan h'iwn guu'lal.\n[q4]Bénédiction du Dieu très ancien\nLié quand ramassé\nJouet\n[q2]Utilise: Reçoit la Bénédiction du Dieu très ancien pendant 20 sec. (10 Min Recharge)"}
MultiLanguageSpellData['fr'][91215] = {name = "[q4]Boîte à secret de Yogg-Saron", additional_info = "3 s d'incantation 15\n[q]Cette petite boîte dispose de nombreux panneaux coulissants, de charnières amovibles et de fermoirs masqués. Mais peu importe l'ordre dans lequel vous les manipulez, vous n'arrivez pas à ouvrir la boîte pour découvrir les secrets qu'elle renferme. Vous avez même des difficultés à essayer, car le sombre artéfact vous chuchote constamment « C'était de TA faute. C'était de TA faute. C'était de TA faute. C'était de TA faute. »\n[q4]Boîte à secret de Yogg-Saron\nLié quand ramassé\nJouet\n[q2]Utilise: Essaye en vain d'ouvrir la boîte à secret. (1 Min Recharge)\n[q]\"Extrêmement difficile à ouvrir.\""}
